(function (o) {
    "use strict";

    var that = {
        degsToRads: function (degs) {
            return degs * Math.PI/180;
        },
        hexToRGB: function (hex) {
            var result = /^#?([a-f\d]{2})([a-f\d]{2})([a-f\d]{2})$/i.exec(hex);
            if (result) {
                return {
                    r: parseInt(result[1], 16),
                    g: parseInt(result[2], 16),
                    b: parseInt(result[3], 16)
                };
            }
            return;
        },
        radsToDegs: function (rads) {
            return rads * 180/Math.PI;
        },
        rand: function (min, max) {
            return Math.random() * (max - min) + min;
        },
        rgbToHex: function (r, g, b) {
            return "#" + ((1 << 24) + (r << 16) + (g << 8) + b).toString(16).slice(1);
        },
        // Fisher yates array shuffle
        shuffle: function(arr) {
            var i = arr.length, j, tempi, tempj;
            if (i === 0) { return false; }
            while (--i) {
                j = Math.floor(Math.random() * ( i + 1 ));
                tempi = arr[i];
                tempj = arr[j];
                arr[i] = tempj;
                arr[j] = tempi;
            }
            return arr;
        },
        // Shuffle the cahracters of a string using the fisher yates shuffle
        shuffleString: function(str) {
            if (typeof str === "undefined" || !str || !str.length) {
                return str;
            } 
            var myStr = str.toString();
            return that.shuffle(myStr.split("")).join("");
        }
    };
    o.core.extend(that);
}(oak));
